const doctors = {};

for (const item of items) {
    const email = item.json.doctor_email;
    if (!doctors[email]) {
        doctors[email] = {
            doctor_nombre: item.json.doctor_nombre,
            doctor_email: email,
            citas: []
        };
    }
    doctors[email].citas.push(`<li><b>${item.json.cita_hora.split(' ')[1]}</b>: ${item.json.paciente_nombre} ${item.json.paciente_apellido}</li>`);
}

return Object.values(doctors).map(d => ({
    json: {
        ...d,
        citas_lista: d.citas.join('')
    }
}));
